mapper.update:
UPDATE products
SET
  last_version_created_at=#{now}
WHERE
  id = #{id}
;

mapper.select:
SELECT p.id AS id, r.price as price, p.pricetag_id AS pricetag_id, p.category_id AS category_id, p.app_id AS app_id, p.created_at AS create_at, p.updated_at AS updated_at, p.last_version_created_at AS last_version_created_at, p.default_locale AS default_locale, p.available_locales AS available_locales, p.author_id AS author_id, p.author_name AS author_name, p.author_email AS author_email, p.author_phone AS author_phone, p.author_website AS author_website, p.`name` AS `name`, p.description AS description, p.logo_image AS logo_image, p.cover_image AS cover_image, p.promotion_image AS promotion_image, p.screenshot1_image AS screenshot1_image, p.screenshot2_image AS screenshot2_image, p.screenshot3_image AS screenshot3_image, p.screenshot4_image AS screenshot4_image, p.screenshot5_image AS screenshot5_image, p.type1 AS type1, p.type2 AS type2, p.type3 AS type3, p.tags AS tags, p.publish_channels AS publish_channels, c.name AS category_name, c.available_tags AS available_tags, c.available_mods AS available_mods FROM products as p LEFT JOIN product_pricetags AS r ON p.app_id = r.app_id AND p.pricetag_id = r.pricetag_id AND p.category_id = r.category_id LEFT JOIN product_categories AS c ON p.app_id = c.app_id WHERE p.app_id = c.app_id AND p.category_id = c.category_id AND p.app_id=#{app_id}
AND p.category_id=#{category_id}
AND p.author_id=#{author_id} ;

SELECT product_id AS id, version, created_at, updated_at, `status`, min_app_version, max_app_version, supported_mod, beta,
`action`, url, file_size, file_md5, dependencies,
version_name, recent_change FROM product_versions WHERE product_id=#{id} ;

SELECT
EXISTS(
SELECT
*
FROM
orders
WHERE
product_id = #{id}

  AND product_version = #{version}

) AS order_exists;

mapper.update:
UPDATE product_versions
SET

  `status` = `status` & 10


  `status` = `status` | 01


WHERE
product_id = #{product_id}

  AND
  version = #{version}

;

mapper.select:
SELECT
product_id AS id,
version,
`status`
FROM
product_versions
WHERE
product_id = #{id}

  AND version = #{version}

;
